import React, { Component } from 'react';
import { Table, Button , Header , Container } from 'semantic-ui-react';
import { Link } from 'react-router-dom';
import {products} from '../products';
import { connect } from 'react-redux';
import { fetchPosts } from '../actions/postActions';
class ProductList extends Component {
    // constructor(props){
    //     super(props);
    //     this.state = {
    //         productList : []
    //     }
    // }
    // componentWillMount(){
    //     this.setState({productList: products})

    // }
    componentWillMount(){
        this.props.fetchPosts();
    }

    render() {
        const productElement = this.state.productList.map( (product, index) => (
            <Table.Row key={index}>
                <Table.Cell>{product.name}</Table.Cell>
                <Table.Cell>{product.weight}</Table.Cell>
                <Table.Cell>{product.availability}</Table.Cell>
                <Table.Cell>{product.isEditable && (<Link  to="/editproduct"><Button primary>Edit</Button></Link>)}</Table.Cell>
            </Table.Row>
        ));
        return (
            <div>
                <React.Fragment>
                    <Container>
                        <Header size='large'>Mobile Products</Header>
                        <Table celled>
                            <Table.Header>
                            <Table.Row>
                                <Table.HeaderCell>Name</Table.HeaderCell>
                                <Table.HeaderCell>Weight</Table.HeaderCell>
                                <Table.HeaderCell>Availability</Table.HeaderCell>
                                <Table.HeaderCell>isEditable</Table.HeaderCell>
                            </Table.Row>
                            </Table.Header>

                            <Table.Body>
                                {productElement}
                            </Table.Body>
                        </Table>
                    </Container>

                </React.Fragment>
            </div>
        )
    }
}
export default connect(null, { fetchPosts })(ProductList);
