import { FETCH_PRODUCT, EDIT_PRODUCT, SUBMIT_PRODUCT } from '../actions/types';
import {products} from '../products';
const initialState = {
    items: products
};
export default function(state = [], action){
    switch(action.type){
        case FETCH_PRODUCT:
            return {
                ...initialState,
                items: action.payload
            }
        case EDIT_PRODUCT:
            const updatedList = state.items.map((item,index) => {
                if(index === action.id){
                    return {
                         ...action.payload 
                    }
                }
                return item
            });
            // console.log(updatedList);
            return {
                items: updatedList
            }
        case SUBMIT_PRODUCT:
            return {
                ...state,
                items: action.payload
            }
        default:
            return state;
    }
}
