import React, { Component } from 'react'
import { Button, Checkbox, Form, Container, Radio, Dropdown } from 'semantic-ui-react'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { editProduct } from '../actions/productActions';
import {pricingInfo} from '../products';
const countryOptions = [
    { key: 'af', value: 'af', flag: 'af', text: 'Afghanistan' },
    { key: 'ax', value: 'ax', flag: 'ax', text: 'Aland Islands' },
    { key: 'al', value: 'al', flag: 'al', text: 'Albania' },
    { key: 'dz', value: 'dz', flag: 'dz', text: 'Algeria' },
    { key: 'as', value: 'as', flag: 'as', text: 'American Samoa' },
    { key: 'ad', value: 'ad', flag: 'ad', text: 'Andorra' }
]
class EditProduct extends Component {

    constructor(props){
        super(props);
        const { id } = this.props.location.state;
        this.state = {
            name: this.props.products[id].name,
            weight: this.props.products[id].weight,
            availability: this.props.products[id].availability,
            product_url: this.props.products[id].productUrl,
            price_tier: this.props.products[id].pricingTier,
            price_range: this.props.products[id].priceRange,
            editable: this.props.products[id].isEditable,
            priceRangeOptions : (this.props.products[id].pricingTier === 'premier') ? pricingInfo.premier : pricingInfo.budget,
            stateOptions : pricingInfo.premier.map( function(value,index){
                return {
                    key: index,
                    value: value,
                    text: value
                }
            })
        };
        console.log(this.state.stateOptions);
        console.log(this.props.products[id]);
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    onChange(e){
        this.setState({[e.target.name]: e.target.value});
    }
    onSubmit(e){
        e.preventDefault();
        const products = {
            name: this.state.name,
            weight: this.state.weight,
            availability: this.state.availability,
            product_url: this.state.product_url,
            price_tier: this.state.price_tier,
            price_range: this.state.price_range

        };
        this.props.editProduct(products);
    }
    handleChange = (e, { value }) => this.setState({ price_tier: value })
    toggle = () => this.setState({ editable: !this.state.editable })
    render() {
        return (
            <React.Fragment>
                <Container>
                <Form onSubmit={this.onSubmit}>
                    <Form.Field>
                    <label>Name</label>
                    <input name='name' value={this.state.name} onChange={this.onChange} required/>
                    </Form.Field>
                    <Form.Field>
                    <label>Weight</label>
                    <input name='weight' value={this.state.weight} onChange={this.onChange} required/>
                    </Form.Field>
                    <Form.Field>
                    <label>Availability</label>
                    <input name='availability' value={this.state.availability} onChange={this.onChange}/>
                    </Form.Field>
                    <Form.Field>
                    <label>Product URL</label>
                    <input name='product_url' value={this.state.product_url} onChange={this.onChange} required/>
                    </Form.Field>
                    <Form.Field>
                    <label>Price Tier</label>
                    <Radio label='Budget' name='radioGroup' value='budget' checked={this.state.price_tier === 'budget'} onChange={this.handleChange}/>
                    <Radio label='Premier' name='radioGroup' value='premier'  checked={this.state.price_tier === 'premier'} onChange={this.handleChange}/>
                    </Form.Field>
                    <Form.Field>
                    <label>Price Range</label>
                    <Dropdown placeholder={this.state.price_range} fluid selection options={this.state.stateOptions}/>
                    </Form.Field>
                    <Form.Field>
                    <Checkbox label='Is Editable' onChange={this.toggle} checked={this.state.editable} required/>
                    </Form.Field>
                    <Button type='submit'>Submit</Button>
                </Form>
                </Container>
            </React.Fragment>
        )
    }
    }
    EditProduct.propTypes = {
        editProduct: PropTypes.func.isRequired,
        products: PropTypes.array.isRequired,
    }
    const mapStateToProps = state => ({
        products: state.products.items
    });

    export default connect(mapStateToProps, { editProduct })(EditProduct);
