import { FETCH_PRODUCT, NEW_PRODUCT } from './types';
import {products} from '../products';
export function fetchProducts(){
    return function(dispatch){
        let productList = products;
        return dispatch({
            type: FETCH_PRODUCT,
            payload: productList
        });
    }
}
export function editProduct(product){
    return function(dispatch){
        let productList = products;
        return dispatch({
            type: NEW_PRODUCT,
            payload: productList
        });
    }
}
