import React, { Component } from 'react'
import { Button, Checkbox, Form, Container, Radio, Dropdown } from 'semantic-ui-react'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import {pricingInfo} from '../products';
import { fetchProducts } from '../actions/productActions';
class EditProduct extends Component {
    productList;
    // id;
    constructor(props){
        super(props);
        const id  = this.props.location.state;
        // console.log(this.props.products);
        this.state = {
            name: '',
            weight: '',
            availability: 0,
            product_url: '',
            price_tier: '',
            price_range: [],
            editable: true,
            priceRangeOptions : []   
        };

        console.log(this.props);

        // this.state = {
        //     name: this.props.products[id].name,
        //     weight: this.props.products[id ].weight,
        //     availability: this.props.products[id ].availability,
        //     product_url: this.props.products[id].productUrl,
        //     price_tier: this.props.products[id ].pricingTier,
        //     price_range: this.props.products[id].priceRange,
        //     editable: this.props.products[id].isEditable,
        //     priceRangeOptions : (this.props.products[id ].pricingTier === 'premier') ? pricingInfo.premier.map( function(value,index){
        //         return {
        //             key: index,
        //             value: value,
        //             text: value
        //         }
        //         }) : pricingInfo.budget.map( function(value,index){
        //         return {
        //             key: index,
        //             value: value,
        //             text: value
        //         }
        //     })   
        // };

        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    componentWillMount(){
        this.props.fetchProducts();
    //    this.setState({
    //         name: this.props.products[this.id].name,
    //         weight: this.props.products[this.id].weight,
    //         availability: this.props.products[this.id].availability,
    //         product_url: this.props.products[this.id].productUrl,
    //         price_tier: this.props.products[this.id].pricingTier,
    //         price_range: this.props.products[this.id].priceRange,
    //         editable: this.props.products[this.id].isEditable,
    //         priceRangeOptions : (this.props.products[this.id].pricingTier === 'premier') ? pricingInfo.premier.map( function(value,index){
    //             return {
    //                 key: index,
    //                 value: value,
    //                 text: value
    //             }
    //             }) : pricingInfo.budget.map( function(value,index){
    //             return {
    //                 key: index,
    //                 value: value,
    //                 text: value
    //             }
    //         })
    //     });
    }
    onChange(e){
        this.setState({[e.target.name]: e.target.value});
    }
    onSubmit(e){
        e.preventDefault();
        const products = {
            name: this.state.name,
            weight: this.state.weight,
            availability: this.state.availability,
            product_url: this.state.product_url,
            price_tier: this.state.price_tier,
            price_range: this.state.price_range

        };
        this.props.fetchProducts();
    }
    handleChange = (e, { value }) => {
        this.setState({ 
        price_tier: value,
        priceRangeOptions : (value=== 'premier') ? pricingInfo.premier.map( function(tier,index){
            return {
                key: index,
                value: tier,
                text: tier
            }
            }) : pricingInfo.budget.map( function(tier,index){
            return {
                key: index,
                value: tier,
                text: tier
            }
        })

    })
}
    toggle = () => this.setState({ editable: !this.state.editable })
    render() {
        return (
            <React.Fragment>
                <Container>
                <Form onSubmit={this.onSubmit}>
                     <Form.Field>
                    <label>Name</label>
                    <input name='name' value={this.props.products[1].name} onChange={this.onChange} required/>
                    </Form.Field> 
                     <Form.Field>
                    <label>Weight</label>
                    <input name='weight' value={this.state.weight} onChange={this.onChange} required/>
                    </Form.Field>
                    <Form.Field>
                    <label>Availability</label>
                    <input name='availability' value={this.state.availability} onChange={this.onChange}/>
                    </Form.Field>
                    <Form.Field>
                    <label>Product URL</label>
                    <input name='product_url' value={this.state.product_url} onChange={this.onChange} required/>
                    </Form.Field>
                    <Form.Field>
                    <label>Price Tier</label>
                    <Radio label='Budget' name='radioGroup' value='budget' checked={this.state.price_tier === 'budget'} onChange={this.handleChange}/>
                    <Radio label='Premier' name='radioGroup' value='premier'  checked={this.state.price_tier === 'premier'} onChange={this.handleChange}/>
                    </Form.Field>
                    <Form.Field>
                    <label>Price Range</label>
                    <Dropdown placeholder={this.state.price_range} fluid selection options={this.state.priceRangeOptions}/>
                    </Form.Field>
                    <Form.Field>
                    <Checkbox label='Is Editable' onChange={this.toggle} checked={this.state.editable} required/>
                    </Form.Field>
                    <Button type='submit'>Submit</Button> 
                </Form>
                </Container>
            </React.Fragment>
        )
    }
    }
    EditProduct.propTypes = {
        fetchProducts: PropTypes.func.isRequired,
        products: PropTypes.array.isRequired,
    }
    const mapStateToProps = (state) => ({
        products: state.products.items
    });

    export default connect(mapStateToProps, { fetchProducts })(EditProduct);
