import React, { Component } from 'react'
import { Button, Checkbox, Form, Container } from 'semantic-ui-react'
export default class EditProduct extends Component {
    constructor(props){
        super(props);
        this.state = {
            name: '',
            weight: '',
            availability: '',
            product_url: '',
            price_tier: '',
            price_range: '',
            editable: ''
        };
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    onChange(e){
        this.setState({[e.target.name]: e.target.value});
    }
    onSubmit(e){
        e.preventDefault();
        const product = {
            name: this.state.name,
            weight: this.state.weight,
            availability: this.state.availability,
            product_url: this.state.product_url,
            price_tier: this.state.price_tier,
            price_range: this.state.price_range

        };
    }
    render() {
        return (
            <React.Fragment>
                <Container>
                <Form onSubmit={this.onSubmit}>
                    <Form.Field>
                    <label>Name</label>
                    <input name='name' value={this.state.name} onChange={this.onChange}/>
                    </Form.Field>
                    <Form.Field>
                    <label>Weight</label>
                    <input name='weight' value={this.state.weight} onChange={this.onChange}/>
                    </Form.Field>
                    <Form.Field>
                    <label>Availability</label>
                    <input name='availability' value={this.state.availability} onChange={this.onChange}/>
                    </Form.Field>
                    <Form.Field>
                    <label>Product URL</label>
                    <input name='product_url' value={this.state.product_url} onChange={this.onChange}/>
                    </Form.Field>
                    <Form.Field>
                    <label>Price Tier</label>
                    <input name='price_tier' value={this.state.price_tier} onChange={this.onChange}/>
                    </Form.Field>
                    <Form.Field>
                    <label>Price Range</label>
                    <input name='price_range' value={this.state.price_range} onChange={this.onChange}/>
                    </Form.Field>
                    <Form.Field>
                    <Checkbox label='Is Editable' />
                    </Form.Field>
                    <Button type='submit'>Submit</Button>
                </Form>
                </Container>
            </React.Fragment>
        )
    }
    }
