import { FETCH_PRODUCT, EDIT_PRODUCT, SUBMIT_PRODUCT } from '../actions/types';
import {products} from '../products';
const initialState = {
    items: products
};
export default function(state = initialState, action){
    switch(action.type){
        case FETCH_PRODUCT:
            return {
                ...state,
                items: action.payload
            }
        case EDIT_PRODUCT:
            const productList = products;
            const updatedList = productList.map((item,index) => {
                if(index === action.payload.id){
                    return { ...item, ...action.payload }
                }
                return item
            });
            console.log(updatedList);
            // console.log(Object.assign({}, ...state, {items: action.payload}));
            // return Object.assign({}, state, {items: action.payload});
            // return {
            //     ...state,
            //     items: []
            // }
            return updatedList;
        case SUBMIT_PRODUCT:
            return {
                ...state,
                items: action.payload
            }
        default:
            return state;
    }
}
