import { FETCH_PRODUCT, EDIT_PRODUCT, SUBMIT_PRODUCT } from '../actions/types';
import {products} from '../products';
const initialState = {
    items: products
};
export default function(state = initialState, action){
    switch(action.type){
        case FETCH_PRODUCT:
            return {
                ...state,
                items: action.payload
            }
        case EDIT_PRODUCT:
            // console.log(Object.assign({}, ...state, {items: action.payload}));
            // return Object.assign({}, state, {items: action.payload});
            return {
                ...state,
                items: []
            }
        case SUBMIT_PRODUCT:
            return {
                ...state,
                items: action.payload
            }
        default:
            return state;
    }
}
